import React, { useState } from "react";
import { motion, AnimatePresence } from "framer-motion";

export default function Contact() {
  const [formData, setFormData] = useState({
    name: "",
    emailOrPhone: "",
    technology: "",
    description: ""
  });

  const [successMessage, setSuccessMessage] = useState(null);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const res = await fetch(`${process.env.REACT_APP_API}/api/leads`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(formData)
    });

    if (res.ok) {
      const timestamp = new Date().toLocaleString();
      setSuccessMessage(`✅ Request submitted! We’ll respond within 24 hours. (Submitted at ${timestamp})`);
      setFormData({ name: "", emailOrPhone: "", technology: "", description: "" });

      // Auto-dismiss after 5 seconds
      setTimeout(() => setSuccessMessage(null), 5000);
    } else {
      alert("Something went wrong.");
    }
  };

  return (
    <div className="max-w-xl mx-auto mt-8">
      <h2 className="text-2xl font-bold mb-4">Request Support</h2>

      <AnimatePresence>
        {successMessage && (
          <motion.div
            key="success"
            initial={{ y: -20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            exit={{ y: -20, opacity: 0 }}
            transition={{ duration: 0.4 }}
            className="mb-4 p-4 rounded border border-green-400 bg-green-100 text-green-800 relative shadow"
          >
            {successMessage}
            <button
              onClick={() => setSuccessMessage(null)}
              className="absolute top-2 right-3 text-green-700 font-bold text-xl"
            >
              &times;
            </button>
          </motion.div>
        )}
      </AnimatePresence>

      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          name="name"
          placeholder="Your Name"
          value={formData.name}
          onChange={handleChange}
          className="w-full p-2 border rounded"
          required
        />
        <input
          name="emailOrPhone"
          placeholder="Email or Phone"
          value={formData.emailOrPhone}
          onChange={handleChange}
          className="w-full p-2 border rounded"
          required
        />
        <input
          name="technology"
          placeholder="Technology"
          value={formData.technology}
          onChange={handleChange}
          className="w-full p-2 border rounded"
          required
        />
        <textarea
          name="description"
          placeholder="Brief Description"
          value={formData.description}
          onChange={handleChange}
          className="w-full p-2 border rounded"
          required
        />
        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Submit
        </button>
      </form>
    </div>
  );
}
