import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function AdminLogin() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch("http://localhost:8080/api/admin/login", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password })
      });

      if (res.ok) {
        const token = await res.text();

        console.log("‚úÖ Login successful");
        console.log("üîê JWT token:", token);

        if (!token.startsWith("eyJ")) {
          alert("‚ö†Ô∏è Token format unexpected. Check backend response.");
          return;
        }

        localStorage.setItem("adminToken", token);
        navigate("/dashboard");
      } else {
        console.error("‚ùå Login failed. Status:", res.status);
        alert("Invalid credentials. Try again.");
      }
    } catch (err) {
      console.error("üö´ Login request failed:", err);
      alert("Login failed. Network or CORS issue.");
    }
  };

  return (
    <div className="max-w-sm mx-auto mt-20">
      <h2 className="text-2xl font-bold mb-4 text-center">Admin Login</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="w-full p-2 border rounded"
          required
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="w-full p-2 border rounded"
          required
        />
        <button
          type="submit"
          className="w-full bg-blue-700 text-white py-2 rounded hover:bg-blue-800 transition"
        >
          Login
        </button>
      </form>
    </div>
  );
}
