import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Contact() {
  const [formData, setFormData] = useState({
    name: "",
    emailOrPhone: "",
    technology: "",
    description: ""
  });
  const [loading, setLoading] = useState(false);
  const [successMsg, setSuccessMsg] = useState("");
  const navigate = useNavigate();

  const isEmail = (input) => /\S+@\S+\.\S+/.test(input);
  const isPhone = (input) => /^[6-9]\d{9}$/.test(input);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate input
    if (!isEmail(formData.emailOrPhone) && !isPhone(formData.emailOrPhone)) {
      alert("Please enter a valid email or phone number.");
      return;
    }

    setLoading(true);

    try {
      const res = await fetch(`${process.env.REACT_APP_API}/api/leads`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData)
      });

      if (res.ok) {
        setSuccessMsg("✅ Your request was submitted successfully! We'll respond within 24 hours.");

        // Optionally trigger WhatsApp
        const message = `Hi, I just submitted a request on itjobsupport.com. My name is ${formData.name}, and I need support for ${formData.technology}.`;
        window.open(`https://wa.me/91XXXXXXXXXX?text=${encodeURIComponent(message)}`, "_blank");

        setFormData({ name: "", emailOrPhone: "", technology: "", description: "" });

        // Redirect after 3 seconds
        setTimeout(() => {
          navigate("/");
        }, 3000);
      } else {
        alert("Something went wrong. Please try again.");
      }
    } catch (err) {
      alert("Network error. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="relative max-w-xl mx-auto mt-10">
      {/* Loader Overlay */}
      {loading && (
        <div className="fixed inset-0 bg-black bg-opacity-40 flex justify-center items-center z-50">
          <div className="w-16 h-16 border-4 border-blue-500 border-t-transparent rounded-full animate-spin"></div>
        </div>
      )}

      {/* Success Message */}
      {successMsg && (
        <div className="mb-4 px-4 py-3 text-green-800 bg-green-100 border border-green-400 rounded animate-bounce">
          {successMsg}
        </div>
      )}

      <h2 className="text-2xl font-bold mb-6 text-center">Request IT Support</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          name="name"
          placeholder="Your Name"
          value={formData.name}
          onChange={handleChange}
          className="w-full p-3 border rounded"
          required
        />
        <input
          name="emailOrPhone"
          placeholder="Email or Phone"
          value={formData.emailOrPhone}
          onChange={handleChange}
          className="w-full p-3 border rounded"
          required
        />
        <input
          name="technology"
          placeholder="Technology (e.g., Java, AWS)"
          value={formData.technology}
          onChange={handleChange}
          className="w-full p-3 border rounded"
          required
        />
        <textarea
          name="description"
          placeholder="Brief Description"
          value={formData.description}
          onChange={handleChange}
          className="w-full p-3 border rounded"
          required
        />
        <button
          type="submit"
          className="bg-blue-700 text-white px-4 py-2 rounded hover:bg-blue-800 w-full"
        >
          Submit Request
        </button>
      </form>
    </div>
  );
}
