import React from "react";
import { motion } from "framer-motion";
import { Facebook, Twitter, Linkedin, Mail } from "lucide-react";

export default function Footer() {
  return (
    <motion.footer
      className="bg-gray-100 py-6 mt-10 border-t border-gray-200"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      transition={{ duration: 0.6 }}
    >
      <div className="max-w-6xl mx-auto px-4 text-center">
        <p className="text-sm text-gray-500 mb-3">
          Â© {new Date().getFullYear()}{" "}
          <span className="font-semibold text-gray-700">
            PRS Technology Software and Services
          </span>. All rights reserved.
        </p>

        <div className="flex justify-center gap-6 mt-2">
          <a href="https://facebook.com" target="_blank" rel="noopener noreferrer" className="text-gray-500 hover:text-blue-600 transition">
            <Facebook size={20} />
          </a>
          <a href="https://twitter.com" target="_blank" rel="noopener noreferrer" className="text-gray-500 hover:text-blue-400 transition">
            <Twitter size={20} />
          </a>
          <a href="https://linkedin.com" target="_blank" rel="noopener noreferrer" className="text-gray-500 hover:text-blue-700 transition">
            <Linkedin size={20} />
          </a>
          <a href="mailto:support@prs.com" className="text-gray-500 hover:text-red-600 transition">
            <Mail size={20} />
          </a>
        </div>
      </div>
    </motion.footer>
  );
}
