import React, { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function Contact() {
  const [formData, setFormData] = useState({
    name: "",
    emailOrPhone: "",
    technology: "",
    description: ""
  });
  const [loading, setLoading] = useState(false);
  const [showSuccess, setShowSuccess] = useState(false);
  const navigate = useNavigate();

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const isPhoneNumber = (value) => /^[6-9]\d{9}$/.test(value);
  const isEmail = (value) =>
    /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value);

  const handleSubmit = async e => {
    e.preventDefault();

    const { name, emailOrPhone, technology, description } = formData;

    if (!name || !emailOrPhone || !technology || !description) {
      alert("All fields are required");
      return;
    }

    if (!isPhoneNumber(emailOrPhone) && !isEmail(emailOrPhone)) {
      alert("Please enter a valid phone number or email");
      return;
    }

    setLoading(true);
    try {
      const res = await fetch(`${process.env.REACT_APP_API}/api/leads`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(formData)
      });

      if (res.ok) {
        setShowSuccess(true);

        if (isPhoneNumber(emailOrPhone)) {
          const whatsappUrl = `https://wa.me/91${emailOrPhone}?text=${encodeURIComponent(
            `Hi, I have submitted the job support form for ${technology}. Please get back to me.`
          )}`;
          setTimeout(() => {
            window.open(whatsappUrl, "_blank");
          }, 1000);
        }

        setTimeout(() => {
          navigate("/");
        }, 3000);
      } else {
        alert("Something went wrong");
      }
    } catch (err) {
      console.error("Submission error:", err);
      alert("Failed to submit form");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-xl mx-auto mt-8 relative">
      {loading && (
        <div className="fixed inset-0 bg-black bg-opacity-40 z-50 flex items-center justify-center">
          <div className="w-16 h-16 border-4 border-white border-dashed rounded-full animate-spin"></div>
        </div>
      )}

      {showSuccess && (
        <div className="bg-green-500 text-white p-3 rounded mb-4 animate-pulse text-center">
          ✅ Submitted! We’ll respond within 24 hours.
        </div>
      )}

      <h2 className="text-2xl font-bold mb-4">Request Support</h2>
      <form onSubmit={handleSubmit} className="space-y-4">
        <input
          name="name"
          placeholder="Your Name"
          value={formData.name}
          onChange={handleChange}
          className="w-full p-2 border rounded"
          required
        />
        <input
          name="emailOrPhone"
          placeholder="Email or Phone"
          value={formData.emailOrPhone}
          onChange={handleChange}
          className="w-full p-2 border rounded"
          required
        />
        <input
          name="technology"
          placeholder="Technology"
          value={formData.technology}
          onChange={handleChange}
          className="w-full p-2 border rounded"
          required
        />
        <textarea
          name="description"
          placeholder="Brief Description"
          value={formData.description}
          onChange={handleChange}
          className="w-full p-2 border rounded"
          required
        />
        <button
          type="submit"
          className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Submit
        </button>
      </form>
    </div>
  );
}
